package ejercicio4;

public class Ejercicio4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		byte a = 0;
		
		short b = 1;
		
		int c = 2;
		
		char d = 'C';
		
		double e = 0.1;
		
		float f = (float) 0.121;
		
		String g = "Carácteres";
		
		System.out.println("El valor de la variable de tipo byte (a) es: " + a);
		
		System.out.println("El valor de la variable de tipo short (b) es: " + b);
		
		System.out.println("El valor de la variable de tipo entero (c) es: " + c);
		
		System.out.println("El valor de la variable de tipo carácter (d) es: " + d);
		
		System.out.println("El valor de la variable de tipo número decimal (e) es: " + e);
		
		System.out.println("El valor de la variable de tipo número foltante (f) es: " + f);
		
		System.out.println("El valor de la variable de tipo cadena de carácteres (g) es: " + g);
	}

}
